export const getWorkingGroupEventPayload = (
  id: string,
  data: Partial<WorkingGroup>,
): WorkingGroupPayload => ({
  type: 'WorkingGroupsUpdated',
  timestamp: '2021-01-01T00:00:00.000Z',
  payload: {
    id,
    $type: 'EnrichedContentEvent',
    type: 'Updated',
    created: '2021-01-01T00:00:00.000Z',
    lastModified: '2021-01-01T00:00:00.000Z',
    version: 1,
    data: {
      description: { iv: 'description' },
      shortText: { iv: 'short text' },
      leaders: { iv: [] },
      members: { iv: [] },
      calendars: { iv: [] },
      title: { iv: 'Working Group Title' },
      complete: { iv: false },
      deliverables: { iv: [] },
      lastModifiedDate: { iv: '2021-01-01T00:00:00.000Z' },
      ...parseToSquidex(data),
    },
  },
});

export const getWorkingGroupEvent = (
  data = {},
): EventBridgeEvent<WorkingGroupEvent, WorkingGroupPayload> =>
  createEventBridgeEventMock(
    getWorkingGroupEventPayload('123', data),
    'WorkingGroupsUpdated',
    '123',
  );

