import { gp2 } from '@asap-hub/model';
import {
  FetchExternalUserQuery,
  FetchExternalUsersQuery,
} from '../../src/autogenerated-gql/graphql';

export const getExternalUserCreateDataObject =
  (): gp2.ExternalUserCreateDataObject => ({
    name: 'External User',
    orcid: 'orcid-1',
  });
export const getExternalUserDataObject = (): gp2.ExternalUserDataObject => ({
  name: 'External User',
  orcid: 'orcid-1',
  id: 'id-1',
});

export const getGraphQLExternalUser = (): NonNullable<
  FetchExternalUserQuery['findExternalUsersContent']
> => ({ id: 'id-1', flatData: { name: 'External User', orcid: 'orcid-1' } });

export const generateGraphqlFetchExternalUsersResponse = (
  items: NonNullable<FetchExternalUserQuery['findExternalUsersContent']>[],
): FetchExternalUsersQuery => ({
  queryExternalUsersContentsWithTotal: {
    total: items.length,
    items,
  },
});
export const getSquidexExternalUsersGraphqlResponse =
  (): FetchExternalUsersQuery =>
    generateGraphqlFetchExternalUsersResponse([getGraphQLExternalUser()]);

export const getExternalUserResponse = (): gp2.ExternalUserResponse => ({
  displayName: 'External User',
  orcid: 'orcid-1',
  id: 'id-1',
});

export const getFetchExternalUsersResponse =
  (): gp2.ListExternalUserResponse => ({
    total: 1,
    items: [getExternalUserResponse()],
  });
