export const getUserCreateDataObject = (): gp2.UserCreateDataObject => {
  const {
    id: _id,
    createdDate: _createdDate,
    contributingCohorts,
    ...userCreateDataObject
  } = getUserDataObject();

  return {
    ...userCreateDataObject,

    contributingCohorts: contributingCohorts.map(
      ({ contributingCohortId, role, studyUrl }) => ({
        contributingCohortId,
        role,
        studyUrl,
      }),
    ),
  };
};

export const getUserInput = (): gp2Squidex.InputUser['data'] => {
  const {
    degrees,
    region,
    fundingStreams,
    telephone,
    contributingCohorts,
    questions,
    positions,
    country,
    keywords,
    social,
    ...input
  } = getUserCreateDataObject();

  return {
    ...parseToSquidex(input),
    region: { iv: 'Europe' },
    degree: { iv: ['MPH'] },
    role: { iv: 'Trainee' },
    avatar: { iv: [] },
    connections: { iv: [] },
    telephoneCountryCode: { iv: telephone?.countryCode || '' },
    telephoneNumber: { iv: telephone?.number || '' },
    questions: { iv: questions.map((question) => ({ question })) },
    biography: { iv: input.biography || '' },
    country: { iv: country },
    positions: { iv: positions },
    keywords: { iv: keywords },
    fundingStreams: { iv: fundingStreams || '' },
    contributingCohorts: {
      iv: contributingCohorts.map(
        ({ contributingCohortId, role, studyUrl }) => ({
          id: [contributingCohortId],
          role,
          study: studyUrl || '',
        }),
      ),
    },
    social: { iv: social ? [social] : null },
  };
};

